   @tsexample
      // Define the position
      %position = \"1.0 1.0 1.0\";
      // Define the velocity
      %velocity = \"1.0 0.0 0.0\";
      // Inform the debris object of its new position and velocity
      %debris.init(%position,%velocity);
   @endtsexample)

